mapper.select:
SELECT

FROM passage psg


    psg.type = #{passageType}
    ORDER BY psg.publish_time DESC
    limit 0,#{showPassageNums}

SELECT count(*)
FROM passage psg
WHERE psg.type = #{passageType}

SELECT

FROM passage psg


    psg.type = #{passageType}
    ORDER BY psg.publish_time DESC
    limit #{fromIndex},#{pageSize}

SELECT count(*)
FROM passage psg


    psg.lesson = #{lessonId} AND psg.chapter = #{chapterId} AND psg.type = #{passageType}

SELECT

FROM passage psg


  psg.lesson = #{lessonId}
  AND psg.chapter = #{chapterId}
  AND psg.type = #{passageType}
  limit #{fromIndex},#{pageSize}

SELECT

FROM passage


    lesson = #{lessonId}


    AND titile LIKE concat(concat('%',#{title}),'%')


ORDER BY publish_time DESC
limit #{fromIndex},#{pageSize}

SELECT count(*) FROM passage
lesson = #{lessonId}
AND titile LIKE concat(concat('%',#{title}),'%')

SELECT

FROM passage


    psg.type = #{passageType} AND lesson = #{lessonId}
    ORDER BY psg.publish_time DESC
    limit #{fromIndex},#{pageSize}

SELECT

FROM passage psg


    author = #{author}


ORDER BY publish_time DESC
limit #{fromIndex},#{pageSize}
